import React from 'react';
import styled from 'styled-components';

import { H1 } from '../utils/Text';

const Header = () => {
  return (
    <>
      <HeaderWrapper>
        <HeaderH1 color='white'>Hashigen</HeaderH1>
        <nav>
          <List>
            <ListItem >AAA</ListItem>
            <ListItem >BBB</ListItem>
            <ListItem >CCC</ListItem>
          </List>
        </nav>
      </HeaderWrapper>
    </>
  )
}

interface ListItemProps {
  color?: string
}

const HeaderWrapper = styled.div`
  display: flex;
  align-items: center;
  background-color: #f7786b;
`;

const HeaderH1 = styled(H1)`
  flex-grow: 1;
`;

const List = styled.ul`
  display: flex;
  list-style: none;
`;

const ListItem = styled.li`
  color: ${(props:ListItemProps) => (props.color ? props.color : 'white')};
  margin-right: 0.5em;
  padding: 0.2em 1em;
  border-radius: 0.3em;
  &:hover {
    background-color: #eea29a;
  }
`;

export default Header;
