import React, {Fragment} from 'react';
import styled from 'styled-components';
import { NavLink , Link} from 'react-router-dom';

import { H1 } from '../utils/Text';

const Header = () => {
  return (
    <Fragment>
      <HeaderWrapper>
        <HeaderH1 color='white'>Hashigen</HeaderH1>
        <nav>
          <List>
            <NavLink><ListItem >AAA</ListItem></NavLink>
            <NavLink><ListItem >BBB</ListItem></NavLink>
            <NavLink><ListItem >CCC</ListItem></NavLink>
            {/* <CustomLink><ListItem >AAA</ListItem></CustomLink>
            <CustomLink><ListItem >BBB</ListItem></CustomLink>
            <CustomLink><ListItem >CCC</ListItem></CustomLink> */}
          </List>
        </nav>
      </HeaderWrapper>
    </Fragment>
  )
}

interface ListItemProps {
  color?: string
}

const HeaderWrapper = styled.div`
  display: flex;
  align-items: center;
  background-color: #f7786b;
`;

const HeaderH1 = styled(H1)`
  flex-grow: 1;
  margin-left: 0.5em;
`;

const List = styled.ul`
  display: flex;
  list-style: none;
`;

const CustomLink = styled(Link)`
  text-decoration: none;
`;

const ListItem = styled.li`
  color: ${(props:ListItemProps) => (props.color ? props.color : 'white')};
  margin-right: 0.5em;
  padding: 0.3em 1em;
  border-radius: 0.3em;
  &:hover {
    background-color: #eea29a;
  }
`;

export default Header;
